import React from "react";

export const Card = ({ cards }) => {
    const [randomCard, setRandomCard] = React.useState(0);

    let randomIndex = Math.floor(Math.random() * cards.length);
    console.log(cards[randomIndex])

    return (
        <div  className="card">
            <h1 className="card-name">Dog</h1>
            <h1 className="card-translation">Собака</h1>
            <div className="card-buttons">
                <button className="card-button-yes">I know</button>
                <button className="card-button-no">Don't know</button>
            </div>
        </div>
    )
}
