import React from 'react';
import './style-table.css';

const categories = [
    {name: "Все слова"},
    {name: "Слова для заучивания"},
    {name: "Выученные слова"}, 
]

export const Table = ({ showTable, setShowTable }) => {
    const [activeCategoryIndex, setActiveCategoryIndex] = React.useState(null);
    
    React.useEffect(() => {
        if (activeCategoryIndex) {
            activeCategoryIndex.clasList.add('open-modal')
        }
    },[])

    return <div class={`modal-wrapper ${showTable ? 'open-modal' : ''}`} >
                <div class="modal modal-table">
                    <div class="modal-content">
                        <button onClick={()=>setShowTable(false)} class="modal-button-close">
                        X
                        </button>
                <ul class="modal-categories">
                    {categories.map((el, index) => {
                        return <li onClick={(e) => setActiveCategoryIndex(e.target)} class={`modal-category ${activeCategoryIndex? 'active-category' : ''}`} key={index}>{el.name}</li>
                    })}
                        </ul>
                    </div>
                </div>
            </div>
}
