import React from "react";

export const Card = ({ cards, setCards, setFinish }) => {
    const [randomIndex, setRandomIndex] = React.useState(0);
    const [showResult, setShowResult] = React.useState(false);

    React.useEffect(() => {
        const index = Math.floor(Math.random() * (cards.length));
        setRandomIndex(index)
    }, []);

    function handleShowNextCard(id) { 
        setCards(cards => {
            const updateCards = cards.filter(obj => obj.id !== id)
            if (updateCards.length > 0) {
                const newIndex = Math.floor(Math.random() * (updateCards.length-1))
                setRandomIndex(newIndex)
            } else {
                setFinish(true)
            }
            
            return updateCards
        })
    }
    
    const card = cards[randomIndex]


    function handleShowResult(id) {
        setShowResult(true);

        setTimeout(() => {
            setShowResult(false)
        }, 5000);

        setTimeout(() => {
            handleShowNextCard(id)
        }, 2000);

    }

    return (
        <div key={card.id} id={card.id}  className="card">
            <h1 className="card-name">{card.name}</h1>
            <h1 className={`card-translation ${showResult && 'show-answer'}`}>{card.translation}</h1>
            <div className="card-buttons">
                <button
                    onClick={()=>handleShowNextCard(card.id)}
                    className="card-button-yes"
                >I know
                </button>
                <button
                    onClick={() => handleShowResult(card.id)}
                    className="card-button-no"
                >Don't know
                </button>
            </div>
        </div>
    )
}
