import React from "react";
import './style-card.css';

export const Card = ({ cards, setCards, setFinish }) => {
    const [randomIndex, setRandomIndex] = React.useState(0);
    const [showResult, setShowResult] = React.useState(false);
    const [memorized, setMemorized] = React.useState([]);
    const [stepMix, setStepMix] = React.useState(0);

    React.useEffect(() => {
        const index = Math.floor(Math.random() * (cards.length));
        setRandomIndex(index)
    }, []);

    function handleShowNextCard(id) { 
        setStepMix(stepMix + 1);

        setMemorized(memorized => {
            const updateCards = [...memorized, cards.filter(obj => obj.id === id)].flat()
            return updateCards;
        })

        if (stepMix == 2) {
            const newIndex = Math.floor(Math.random() * (memorized.length - 1));
            setRandomIndex(newIndex);

            setStepMix(0);
        } else {

            setCards(cards => {
                const updateCards = cards.filter(obj => obj.id !== id);
    
                if (updateCards.length > 0) {
                    const newIndex = Math.floor(Math.random() * (updateCards.length - 1));
                    setRandomIndex(newIndex);
                } else {
                    setFinish(true);
                }
                return updateCards;
            });
        }
        
    }

    function handleShowResult(id) {
        setShowResult(true);

        setTimeout(() => {
            setShowResult(false)

            const newIndex = Math.floor(Math.random() * (cards.length - 1))
            setRandomIndex(newIndex)
        }, 5000);
    }
    
    const card = cards[randomIndex];

    return (
        <div key={card.id} id={card.id}  class="card">
            <h1 class="card-name">{card.name}</h1>
            <h1 class={`card-translation ${showResult && 'show-answer'}`}>{card.translation}</h1>
            <div class="card-buttons">
                <button
                    onClick={() => handleShowNextCard(card.id)}
                    class="card-button-yes"
                >I know
                </button>
                <button
                    onClick={() => handleShowResult(card.id)}
                    class="card-button-no"
                >Don't know
                </button>
            </div>
        </div>
    )
}
