import React from "react";

export const Card = ({ cards, setCards }) => {
    const [randomIndex, setRandomIndex] = React.useState(0);

    React.useEffect(() => {
        const index = Math.floor(Math.random() * cards.length);
        setRandomIndex(index)
    }, [])
    
    const card = cards[randomIndex]


    function showResult(id) {
        setCards(cards.map((obj) => {
            if (obj.id === id) {
                obj.classList.add
            }
            return obj
        }))
    }

    return (
        <div key={card.id} id={card.id}  className="card">
            <h1 className="card-name">{card.name}</h1>
            <h1 className="card-translation">{card.translation}</h1>
            <div className="card-buttons">
                <button className="card-button-yes">I know</button>
                <button onClick={showResult(card.id)} className="card-button-no">Don't know</button>
            </div>
        </div>
    )
}
