import React from 'react';
import './index.css';
import { Card } from './Components/Card/Card';

function App() {
  const [cards, setCards] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);
  const [finishGame, setFinish] = React.useState(false);
  const [memorized, setMemorized] = React.useState([]);
  const [showMemory, setShowMemory] = React.useState(false);
  const [randomIndex, setRandomIndex] = React.useState(0);

  React.useEffect(() => {
    fetch('/data.json')
      .then(res => res.json())
      .then(json => {
        setCards(json)
      })
      .catch(err => console.warn('err'))
      .finally(()=>setIsLoading(false))
  }, []);

  function clearStats() {
    window.location.reload();
  }

  console.log(cards)
  console.log(memorized)

  return (
    <div class='App'>
      <header class='header'>
        <h1 class='header__title'>English words</h1>
      </header>
      <div class='content'>
        {isLoading ?
          (<h1>Loading...</h1>)
          : (<>
            {finishGame ?
              <h2>KONEC</h2>
              :
              <Card
                showMemory={showMemory}
                setShowMemory={setShowMemory}
                setMemorized={setMemorized}
                memorized={memorized}
                setFinish={setFinish}
                setCards={setCards}
                cards={cards}
            />
            }
            </>
          ) 
        }  
        <button onClick={()=>clearStats()} class="button-clear">Clear</button>
      </div>
    </div>
  );
}

export default App;
