import React from "react";

export const Card = ({ cards }) => {
    let randomIndex = Math.floor(Math.random() * cards.length);

    return (
        cards[randomIndex].map(obj => {
            return  <div  className="card">
                    <h1 className="card-name">{obj.name}</h1>
                        <h1 className="card-translation">{obj.name}</h1>
                        <div className="card-buttons">
                            <button className="card-button-yes">I know</button>
                            <button className="card-button-no">Don't know</button>
                        </div>
                    </div>
        })
    )
}
